CREATE TRIGGER HZGDefaults after insert on HZGraph when min(new.tSimp, new.tGBFB, new.tTrad, new.tB5FB, new.tJpShin, new.tJISFB) is null begin update HZGraph set tSimp = ifnull(new.tSimp, new.tHanzi), tGBFB = ifnull(new.tGBFB, new.tHanzi), tTrad = ifnull(new.tTrad, new.tHanzi), tB5FB = ifnull(new.tB5FB, new.tHanzi), tJpShin = ifnull(new.tJpShin, new.tHanzi), tJISFB = ifnull(new.tJISFB, new.tHanzi) where tHanzi = new.tHanzi; end;
CREATE TRIGGER HZGParChkIns before insert on HZGraph when new.tParent is not null begin select raise(fail, "non-existent parent") where not exists (select 1 from HZGraph where tHanzi = new.tParent); end;
CREATE TRIGGER HZGParChk before update of tParent on HZGraph when new.tParent is not null begin select raise(fail, "parent loop") where new.tParent = new.tHanzi; select raise(fail, "non-existent parent") where not exists (select 1 from HZGraph where tHanzi = new.tParent); end;
CREATE TRIGGER HZGHzChk before update of tHanzi on HZGraph begin select raise(fail, "parent loop") where new.tParent = new.tHanzi; select raise(fail, "would break parent reference") where exists (select 1 from HZGraph where tParent=old.tHanzi limit 1); end;
CREATE TRIGGER HZGParChkDel before delete on HZGraph begin select raise(fail, "would break parent reference") where exists (select 1 from HZGraph where tParent=old.tHanzi limit 1); end;
CREATE TRIGGER HZMPhChkIns before insert on HZMorph when new.tPhon is not null begin select raise(fail, "non-existent phonetic") where  not exists (select 1 from HZMorph where tHZ = new.tPhon); select raise(fail, "non-existent phonetic pinyin") where  new.tPhPY is not null and not exists (select 1 from HZMorph  where tHZ = new.tPhon and tPY = new.tPhPY);  select raise(fail, "non-existent phonetic MGCR") where  new.tPhPY is not null and new.tPhGC is not null and not exists  (select 1 from HZMorph where tHZ = new.tPhon and   tPY = new.tPhPY and tMGCR = new.tPhGC); end;
CREATE TRIGGER HZMPhChk before update of tPhon, tPhPY, tPhGC on HZMorph when new.tPhon is not null begin select raise(fail, "phonetic loop") where new.tPhon = new.tHZ; select raise(fail, "non-existent phonetic") where  not exists (select 1 from HZMorph where tHZ = new.tPhon); select raise(fail, "non-existent phonetic pinyin") where  new.tPhPY is not null and not exists (select 1 from HZMorph  where tHZ = new.tPhon and tPY = new.tPhPY);  select raise(fail, "non-existent phonetic MGCR") where  new.tPhPY is not null and new.tPhGC is not null and not exists  (select 1 from HZMorph where tHZ = new.tPhon and   tPY = new.tPhPY and tMGCR = new.tPhGC); end;
CREATE TRIGGER HZMHzChk before update of tHZ on HZMorph begin select raise(fail, "protected from updating hanzi"); end;
CREATE TRIGGER HZMPyChk before update of tPY on HZMorph begin select raise(fail, "would break pinyin reference") where exists (select 1 from HZMorph where tPhon = old.tHZ and tPhPY = old.tPY and (tPhGC = old.tMGCR or (tPhGC is null and not exists (select 1 from HZMorph where tHZ = old.tHZ and tPY = old.tPY and tMGCR != old.tMGCR))) limit 1); end;
CREATE TRIGGER HZMMgcrChk before update of tMGCR on HZMorph begin select raise(fail, "would break MGCR reference") where exists (select 1 from HZMorph where tPhon = old.tHZ and tPhPY = old.tPY and tPhGC = old.tMGCR limit 1); end;
CREATE TRIGGER HZMPhChkDel before delete on HZMorph begin select raise(fail, "would break phonetic reference") where exists (select 1 from HZMorph where tPhon = old.tHZ and (tPhPY is null or tPhPY = old.tPY) and (tPhGC is null or tPhGC = old.tMGCR) limit 1); end;
